import React from 'react';
import Sidebar from '../components/Sidebar';
import './ScheduledMaintenance.css';

const ScheduledMaintenance: React.FC = () => {
  // This would eventually come from your server
  const scheduledVehicles = [
    {
      id: 1,
      make: 'Toyota',
      model: 'Camry',
      year: 2019,
      customer: 'Emily Johnson',
      services: [
        { 
          id: 101, 
          name: 'Oil Change', 
          technician: 'Mike Smith',
          status: 'in-progress'
        },
        { 
          id: 102, 
          name: 'Brake Inspection', 
          technician: 'Mike Smith',
          status: 'pending'
        }
      ]
    },
    {
      id: 2,
      make: 'Honda',
      model: 'CR-V',
      year: 2020,
      customer: 'Robert Chen',
      services: [
        { 
          id: 103, 
          name: 'Tire Rotation', 
          technician: 'David Wilson',
          status: 'completed'  
        },
        { 
          id: 104, 
          name: 'Air Filter Replacement', 
          technician: 'David Wilson',
          status: 'in-progress'
        }
      ]
    },
    {
      id: 3,
      make: 'Ford',
      model: 'F-150',
      year: 2018,
      customer: 'Sarah Martinez',
      services: [
        { 
          id: 105, 
          name: 'Transmission Flush', 
          technician: 'Chris Taylor',
          status: 'pending'
        },
        { 
          id: 106, 
          name: 'Battery Replacement', 
          technician: 'James Lee',
          status: 'in-progress'
        }
      ]
    },
    {
      id: 4,
      make: 'BMW',
      model: 'X5',
      year: 2021,
      customer: 'Michael Thompson',
      services: [
        { 
          id: 107, 
          name: 'Diagnostic Scan', 
          technician: 'Lisa Johnson',
          status: 'completed'
        },
        { 
          id: 108, 
          name: 'Coolant Flush', 
          technician: 'Lisa Johnson',
          status: 'pending'
        }
      ]
    }
  ];

  const getStatusClass = (status: string) => {
    switch(status) {
      case 'pending': return 'status-pending';
      case 'in-progress': return 'status-in-progress';
      case 'completed': return 'status-completed';
      default: return '';
    }
  };

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <h1>Scheduled Maintenance</h1>
        <p>Manage and view upcoming maintenance tasks for vehicles in our shop.</p>
        
        <div className="vehicles-grid">
          {scheduledVehicles.map(vehicle => (
            <div key={vehicle.id} className="vehicle-card">
              <div className="vehicle-header">
                <div>{vehicle.make}</div>
                <div className="vehicle-model">{vehicle.model}</div>
              </div>
              
              <div className="vehicle-content">
                <div className="vehicle-info">
                  <div className="vehicle-customer">{vehicle.customer}</div>
                  <div className="vehicle-year">{vehicle.year}</div>
                </div>
                
                <ul className="maintenance-list">
                  {vehicle.services.map(service => (
                    <li key={service.id} className="maintenance-item">
                      <div className="service-name">{service.name}</div>
                      <div className="service-tech">
                        <span>Tech: {service.technician}</span>
                        <span className={`maintenance-status ${getStatusClass(service.status)}`}>
                          {service.status.charAt(0).toUpperCase() + service.status.slice(1)}
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ScheduledMaintenance;
