import React from 'react';
import Sidebar from '../components/Sidebar';
import './CurrentMaintenance.css';

const CurrentMaintenance: React.FC = () => {
  const workOrders = [
    {
      id: 1,
      make: 'Toyota',
      model: 'Camry',
      year: 2019,
      technician: 'John Doe',
      billTotal: 250,
      services: [
        { id: 101, name: 'Oil Change', cost: 50 },
        { id: 102, name: 'Brake Repair', cost: 200 }
      ]
    },
    {
      id: 2,
      make: 'Honda',
      model: 'CR-V',
      year: 2020,
      technician: 'Alex Carter',
      billTotal: 180,
      services: [
        { id: 103, name: 'Tire Rotation', cost: 30 },
        { id: 104, name: 'Air Filter Change', cost: 150 }
      ]
    }
  ];

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <h1>Current Maintenance</h1>
        <p>View the ongoing maintenance tasks in real-time.</p>

        <div className="work-orders">
          {workOrders.map(order => (
            <div key={order.id} className="work-card">
              <div className="card-header">
                <span>{order.make} {order.model} ({order.year})</span>
              </div>
              <div className="card-body">
                <h6>Technician: {order.technician}</h6>
                <button className="change-tech">Change Technician</button>
                <h6>Services:</h6>
                <ul>
                  {order.services.map(service => (
                    <li key={service.id} className="service-item">
                      {service.name} - ${service.cost}
                    </li>
                  ))}
                </ul>
                <h5>Total: ${order.billTotal}</h5>
                <div className="order-actions">
                  <button className="remove-service">Remove Service</button>
                  <button className="add-service">Add Service</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CurrentMaintenance;
