import React from "react";

const CurrentMaintenance = () => {
  const maintenanceRecords = [
    {
      id: 1,
      customer: "Emily Johnson",
      vehicle: { make: "Toyota", model: "Camry", year: 2019 },
      services: [
        { name: "Oil Change", details: "Synthetic oil preferred" },
        { name: "Brake Inspection", details: "Squeaking when braking" },
      ],
      technician: "David Miller",
      startedOn: "Mar 29, 2025",
      paymentStatus: "Paid",
    },
    {
      id: 2,
      customer: "Robert Chen",
      vehicle: { make: "Honda", model: "CR-V", year: 2020 },
      services: [
        { name: "Tire Rotation", details: "Uneven wear on front tires" },
        { name: "Air Filter Replacement", details: "Engine seems to be working harder than usual" },
      ],
      technician: "Sarah Thompson",
      startedOn: "Mar 28, 2025",
      paymentStatus: "Pending",
    },
  ];

  return (
    <div className="main-content">
      <h1>Current Maintenance</h1>
      <p>Review vehicles currently undergoing maintenance and update their status.</p>

      <div className="maintenance-grid">
        {maintenanceRecords.map((record) => (
          <div className="maintenance-card" key={record.id}>
            <div className="vehicle-header">
              <span>{record.vehicle.make}</span>
              <span>{record.vehicle.model}</span>
            </div>
            <div className="maintenance-content">
              <div className="vehicle-info">
                <span className="customer-info">{record.customer}</span>
                <span className="vehicle-year">{record.vehicle.year}</span>
              </div>

              <p><strong>Started On:</strong> {record.startedOn}</p>
              <p><strong>Technician:</strong> {record.technician}</p>
              
              <ul className="active-services">
                {record.services.map((service, index) => (
                  <li key={index} className="service-item">
                    <p className="service-name">{service.name}</p>
                    <p className="service-status">{service.details}</p>
                  </li>
                ))}
              </ul>

              <span className={`bill-total ${record.paymentStatus === "Paid" ? "bill-paid" : "bill-due"}`}>
                {record.paymentStatus}
              </span>

              <div className="maintenance-actions">
                <button className="complete-button">Mark Complete</button>
                <button className="details-button">View Details</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CurrentMaintenance;
