import React, { useState } from 'react';
import Sidebar from '../components/CustSidebar';
import './ContactUs.css';

const ContactUs: React.FC = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', subject: '', message: ''
  });

  const contactDetails = [
    { icon: 'ðŸ“', label: 'Address', text: '123 Auto Drive, Mechanic Valley, CA 94025' },
    { icon: 'ðŸ“ž', label: 'Phone', text: '(555) 123-4567' },
    { icon: 'âœ‰ï¸', label: 'Email', text: 'support@bootcampauto.com' },
    { icon: 'ðŸ•’', label: 'Hours', text: 'Mon-Fri: 8am-6pm, Sat: 9am-4pm, Sun: Closed' }
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    setFormData({ name: '', email: '', phone: '', subject: '', message: '' });
    alert('Message sent successfully!');
  };

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <h1>Contact Us</h1>
        <p>Have questions or need assistance? Reach out to our team.</p>

        <div className="contact-container">
          <div className="form-container">
            <form onSubmit={handleSubmit}>
              {['name', 'email', 'phone', 'subject'].map((field) => (
                <div key={field} className="form-group">
                  <label>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
                  <input
                    type={field === 'email' ? 'email' : 'text'}
                    value={formData[field as keyof typeof formData]}
                    onChange={(e) => setFormData({ ...formData, [field]: e.target.value })}
                    placeholder={`Enter your ${field}`}
                    required={field !== 'phone'}
                  />
                </div>
              ))}
              <div className="form-group">
                <label>Message</label>
                <textarea
                  value={formData.message}
                  onChange={(e) => setFormData({ ...formData, message: e.target.value })}
                  placeholder="How can we help you?"
                  required
                />
              </div>
              <button type="submit" className="submit-button">Send Message</button>
            </form>
          </div>

          <div className="contact-info">
            <h2>Our Information</h2>
            {contactDetails.map((detail) => (
              <div key={detail.label} className="info-item">
                <span className="info-icon">{detail.icon}</span>
                <div>
                  <strong>{detail.label}:</strong>
                  <p>{detail.text}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactUs;
