const ContactUs: React.FC = () => {
  // ... existing state and handlers ...

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <h1>Contact Us</h1>
        <p>Have questions or need assistance? Reach out to our team.</p>

        <div className="contact-container">
          {/* Form Container - Now on top */}
          <div className="form-container">
            {/* Keep existing form structure */}
          </div>

          {/* Contact Info - Now on bottom */}
          <div className="contact-info">
            <h2>Our Information</h2>
            {contactDetails.map((detail) => (
              <div key={detail.label} className="info-item">
                <span className="info-icon">{detail.icon}</span>
                <div>
                  <strong>{detail.label}:</strong>
                  <p>{detail.text}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
