import React, { useState } from 'react';

const ContactUs: React.FC = () => {
  const [form, setForm] = useState({ name: '', email: '', phone: '', subject: '', message: '' });
  const contactInfo = [
    { icon: 'ðŸ“', title: 'Address', content: '123 Auto Drive, Mechanic Valley, CA 94025' },
    { icon: 'ðŸ“ž', title: 'Phone', content: '(555) 123-4567' },
    { icon: 'âœ‰ï¸', title: 'Email', content: 'support@bootcampauto.com' },
    { icon: 'ðŸ•’', title: 'Hours', content: 'Mon-Fri: 8am-6pm\nSat: 9am-4pm\nSun: Closed' }
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Submitted:', form);
    setForm({ name: '', email: '', phone: '', subject: '', message: '' });
    alert('Message sent!');
  };

  return (
    <div style={{ display: 'flex', padding: '2rem' }}>
      <div style={{ flex: 1, paddingRight: '2rem' }}>
        <h1>Contact Us</h1>
        <p>Have questions? Reach out to our team.</p>

        <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '1rem' }}>
          {['name', 'email', 'phone', 'subject'].map(field => (
            <div key={field} style={{ display: 'flex', flexDirection: 'column' }}>
              <label>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
              <input
                type={field === 'email' ? 'email' : 'text'}
                value={form[field as keyof typeof form]}
                onChange={e => setForm({ ...form, [field]: e.target.value })}
                required={field !== 'phone'}
                style={{ padding: '0.875rem', borderRadius: '6px', border: '1px solid #e2e8f0' }}
              />
            </div>
          ))}
          <div style={{ display: 'flex', flexDirection: 'column' }}>
            <label>Message</label>
            <textarea
              value={form.message}
              onChange={e => setForm({ ...form, message: e.target.value })}
              required
              style={{ padding: '0.875rem', borderRadius: '6px', border: '1px solid #e2e8f0' }}
            />
          </div>
          <button type="submit" style={{ backgroundColor: '#3b82f6', color: 'white', padding: '1rem 2rem', border: 'none', borderRadius: '6px', width: '100%' }}>
            Send Message
          </button>
        </form>
      </div>

      <div style={{ flex: 1 }}>
        <h2>Our Information</h2>
        {contactInfo.map(item => (
          <div key={item.title} style={{ display: 'flex', gap: '1rem', padding: '1rem', background: '#f8fafc', borderRadius: '8px', marginBottom: '1rem' }}>
            <span>{item.icon}</span>
            <div>
              <strong>{item.title}</strong>
              <p>{item.content}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ContactUs;
