import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';
import Sidebar from '../components/CustSidebar';

const CustDashboard: React.FC = () => {
  const navigate = useNavigate();
  
  const portalFeatures = [
    { 
      title: 'Request Service', 
      desc: 'Schedule new maintenance or repairs',
      icon: 'üõ†Ô∏è',
      path: '/customer/request-service'
    },
    { 
      title: 'Service History', 
      desc: 'View past service records',
      icon: 'üìñ',
      path: '/customer/service-history'
    },
    { 
      title: 'Contact Support', 
      desc: 'Get in touch with our team',
      icon: 'üìß',
      path: '/customer/contact-us'
    },
  ];

  return (
    <div className="app-container">
      <Sidebar />
      
      <div className="main-content">
        {/* Welcome Card Added Back */}
        <div className="welcome-card">
          <h1>Welcome to AutoCare Portal</h1>
          <p className="welcome-text">
            Manage your vehicle services, track maintenance history, 
            and connect with our expert mechanics. We're here to keep 
            your vehicle running smoothly.
          </p>
        </div>

        {/* Features Grid */}
        <div className="features-grid">
          {portalFeatures.map((feature) => (
            <div 
              key={feature.title}
              className="feature-item"
              onClick={() => navigate(feature.path)}
            >
              <span className="feature-icon">{feature.icon}</span>
              <h3>{feature.title}</h3>
              <p>{feature.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CustDashboard;
